@import url('bootstrap/dist/css/bootstrap.min.css');
@import url('owl.carousel/dist/assets/owl.carousel.css');

@import 'predefinied.scss';

body {
    font-family: 'KelsonSans', sans-serif;
    // font-family: 'Anton', sans-serif;
}


.header {
    position: fixed;
    z-index: 1024;
    width: 100%;
    display: flex;
    // display: none;
    top: 16px;
    transition: 0.5s;

    .container-fluid {
        height: 60px;
        position: relative;
        background: transparent;
        border-radius: .0010px;
        transition: $durationM;

        &.scrolled {
            box-shadow: 0 20px 35px rgba(0, 0, 0, 0.2);
            margin-top: 0.25rem;
            border-radius: 200px;
            background: white;
            padding: 0 0.75rem;


            .header__logo-container {
                margin-left: 1rem;
            }
        }
    }

    &__logo {
        max-width: 100%;
        max-height: 100%;
        position: relative;
        z-index: 10;
        overflow: hidden;

        &-container {
            max-width: 100%;
            max-height: 100%;
            height: 100px;
            margin: 0;
            display: block;
            display: flex;
            justify-content: center;
            align-items: center;
        }
    }


    .nav-toggle {
        z-index: 1024;
        display: none;
        background: none;
        border: 0;
        outline: 0;
        height: 48px;
        align-items: center;

        background: $ptGrey;
        justify-content: center;
        align-items: center;
        position: relative;
        transition: $durationM;
        // box-shadow: 4px 4px 8px $neuShadow1,
        //     -4px -4px 8px $neuShadow2;


        &:active {
            transform: scale(0.9);
        }

        svg {
            // color: $pirateBlack;
            transition: $bezierFastoutSlowin 0.5s;
            top: 50%;
            left: 50%;
            position: absolute;
            width: 28px;
            height: 28px;
            z-index: 1;


            path {
                // fill: $spaceColor;
                transition: $bezierFastoutSlowin 0.5s;
            }
        }

        &__inner {
            position: relative;
        }

        &__icon--menu {
            opacity: 1;
            transform: translate(-50%, -50%);
        }

        &__icon--close {
            opacity: 0;
            transform: translate(-50%, -50%) rotate(45deg);
        }
    }


    .button-icon-only {
        align-items: center;
        justify-content: center;
        padding: 0;
    }


    // &__nav {
    //     flex-grow: 10;
    //     display: flex;
    //     align-items: center;
    //     margin-left: calc(-207px / 2);
    // }

    &__nav-list {
        display: flex;
    }

    &__nav-link {
        color: black;
        align-self: center;
        font-size: $fontSizeBodyL;
        text-decoration: none;
        position: relative;
        transition: color $durationS ease .1s;
        line-height: 1;
        // padding: 0 $spaceS;
        // margin: 0 $spaceS;
        border-radius: 0;
        letter-spacing: $spaceLetters;
        transition: $bezierFastoutSlowin 0.36s;

        &:active,
        &:hover {
            text-decoration: none;

            &:before {
                width: 100%;
            }
        }

        &.active {
            color: white;
        }
    }

    &__nav-left {
        position: relative;

        li {
            list-style-type: none;
            padding: .75rem;
            margin: 0;
            transition: 0.8s;

            &:hover {
                // transform: scale(1.12);
            }

            &:hover~.magic-line {
                // bottom: -1.5rem;
                // opacity: 0.7;
            }
        }

        // .magic-line {
        //     position: absolute;
        //     bottom: .25rem;
        //     left: 0;
        //     padding: 0;
        //     width: 100px;
        //     height: 4px;
        //     border-radius: 200px;
        //     background: $steelBeauGradient;
        //     transition: $durationM;
        //     opacity: 0;
        // }

        .magic-line {
            content: '';
            display: block;
            position: absolute;
            // bottom: calc(50% - 20px);
            // left: calc(50% - 20px);
            // width: 40px;
            // height: 40px;
            width: 16px;
            height: 16px;
            top: -0.75rem;
            left: calc(50% - 6px);
            border-radius: 50%;
            -webkit-transition: 0.5s;
            transition: 0.5s;
            opacity: 0;
            padding: 0;
            // background: $spaceColor;
            // background: $queenBlue;
            transition: 0.8s;
        }
    }

    &__social-link {
        // background: $smallBtnGradient;
        width: 42px;
        height: 42px;
        border-radius: 1rem;
        display: flex;
        justify-content: center;
        align-items: center;
        position: relative;
        transition: $durationM;
        // box-shadow: 4px 4px 8px $neuShadow1,
        //     -4px -4px 8px $neuShadow2;

        &::before {
            opacity: 0;
            content: '';
            position: absolute;
            top: 50%;
            left: 50%;
            width: 100%;
            height: 100%;
            // background: $main-blue;
            // background: $bigBtnHoverGradient;
            transition: $durationM;
            border-radius: 1rem;
            transform: translate(-50%, -50%);
        }

        svg {
            // fill: white;
            // fill: $spaceColor;
            width: 30px;
            height: 20px;
            position: relative;
            z-index: 1;
        }

        &:hover {
            &::before {
                opacity: 1;
            }
        }

        &:active {
            transform: scale(0.9);
        }
    }

}
